+    <script>
+        function filterCourses() {
+            const searchTerm = document.getElementById('search').value.toLowerCase();
+            const programFilter = document.getElementById('program').value;
+            const levelFilter = document.getElementById('level').value;
+            
+            const programSections = document.querySelectorAll('.program-section');
+            const courseCards = document.querySelectorAll('.course-card');
+            
+            // Reset all visibility
+            programSections.forEach(section => section.style.display = 'block');
+            courseCards.forEach(card => card.style.display = 'block');
+            
+            // Apply program filter
+            if (programFilter) {
+                programSections.forEach(section => {
+                    if (!section.getAttribute('data-program').includes(programFilter)) {
+                        section.style.display = 'none';
+                    }
+                });
+            }
+            
+            // Apply course-level filters
+            courseCards.forEach(card => {
+                const cardText = card.textContent.toLowerCase();
+                const cardLevel = card.getAttribute('data-level');
+                
+                let showCard = true;
+                
+                // Search filter
+                if (searchTerm && !cardText.includes(searchTerm)) {
+                    showCard = false;
+                }
+                
+                // Level filter
+                if (levelFilter && cardLevel !== levelFilter) {
+                    showCard = false;
+                }
+                
+                card.style.display = showCard ? 'block' : 'none';
+            });
+            
+            // Hide program sections with no visible courses
+            programSections.forEach(section => {
+                const visibleCards = section.querySelectorAll('.course-card[style="display: block;"]');
+                if (visibleCards.length === 0) {
+                    section.style.display = 'none';
+                }
+            });
+        }
+
+        // Add event listeners for real-time filtering
+        document.getElementById('search').addEventListener('input', filterCourses);
+        document.getElementById('program').addEventListener('change', filterCourses);
